import requests
import json
from datetime import datetime
# 获取npm包的下载量数据
def get_npm_downloads(package_name):
    """获取npm包的周下载量"""
    try:
        url = f"https://api.npmjs.org/downloads/point/last-week/{package_name}"
        response = requests.get(url, timeout=10)
        if response.status_code == 200:
            data = response.json()
            return data.get('downloads', 0)
    except Exception as e:
        print(f"获取 {package_name} npm下载量失败: {e}")
    return 0
# 获取GitHub仓库信息
def get_github_info(repo_owner, repo_name):
    """获取GitHub仓库的星标数和fork数"""
    try:
        url = f"https://api.github.com/repos/{repo_owner}/{repo_name}"
        headers = {'Accept': 'application/vnd.github.v3+json'}
        response = requests.get(url, headers=headers, timeout=10)
        if response.status_code == 200:
            data = response.json()
            return {
                'stars': data.get('stargazers_count', 0),
                'forks': data.get('forks_count', 0),
                'open_issues': data.get('open_issues_count', 0)
            }
    except Exception as e:
        print(f"获取 {repo_owner}/{repo_name} GitHub信息失败: {e}")
    return {'stars': 0, 'forks': 0, 'open_issues': 0}
# 定义要调研的库
libraries = [
    {
        'name': 'Material UI',
        'npm_package': '@mui/material',
        'github': 'mui/material-ui'
    },
    {
        'name': 'Chakra UI',
        'npm_package': '@chakra-ui/react',
        'github': 'chakra-ui/chakra-ui'
    },
    {
        'name': 'Ant Design',
        'npm_package': 'antd',
        'github': 'ant-design/ant-design'
    },
    {
        'name': 'Mantine',
        'npm_package': '@mantine/core',
        'github': 'mantinedev/mantine'
    }
]
# 收集数据
results = []
for lib in libraries:
    print(f"正在调研 {lib['name']}...")

    # 获取npm下载量
    downloads = get_npm_downloads(lib['npm_package'])

    # 获取GitHub信息
    github_owner, github_repo = lib['github'].split('/')
    github_info = get_github_info(github_owner, github_repo)

    results.append({
        'name': lib['name'],
        'npm_package': lib['npm_package'],
        'github': lib['github'],
        'weekly_downloads': downloads,
        'github_stars': github_info['stars'],
        'github_forks': github_info['forks'],
        'open_issues': github_info['open_issues']
    })
# 保存结果到共享文件
utils.save_shared_data("react_ui_libraries_data.json", results)
# 打印结果
print("\n" + "="*80)
print("React UI库流行度调研结果")
print("="*80)
print(f"{'库名称':<15} {'周下载量':<15} {'GitHub Stars':<15} {'GitHub Forks':<15}")
print("-"*80)
for result in results:
    print(f"{result['name']:<15} {result['weekly_downloads']:<15,} {result['github_stars']:<15,} {result['github_forks']:<15,}")
print("="*80)
utils.set_state(success=True, message="流行度数据收集完成")

